import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import mockData from "./mockData";

function EndpointsTable() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchValue, setSearchValue] = useState(
    searchParams.get("search_val") || ""
  );
  const [endpointType, setEndpointType] = useState(
    searchParams.get("endpoint_type") || ""
  );

  useEffect(() => {
    setData(mockData);

    // const filtered = data.filter((item) => {
    //   return (
    //     (!searchValue ||
    //       item.name.toLowerCase().includes(searchValue.toLowerCase())) &&
    //     (!endpointType || item.type === endpointType)
    //   );
    // });

    setFilteredData(filtered);
  }, [data, searchValue, endpointType]);

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleEndpointTypeChange = (e) => {
    setEndpointType(e.target.value);
  };

  return (
    <div className="endpoints">
      <h2>Серверы и ПК</h2>

      <div className="filters">
        <input
          type="text"
          placeholder="Поиск по названию"
          value={searchValue}
          onChange={handleSearchChange}
        />
        <select value={endpointType} onChange={handleEndpointTypeChange}>
          <option value="">Все типы</option>
          <option value="server">Сервер</option>
          <option value="pc">ПК</option>
        </select>
      </div>

      <table>
        <thead>
          <tr>
            <th>Название</th>
            <th>Тип</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EndpointsTable;
