import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import mockData from "./mockData";
import Pagination from "./pagination";

function EndpointsTable() {
  const location = useLocation();
  const history = useNavigate();
  const searchParams = new URLSearchParams(location.search);

  const handleFiltersAndPaginationChange = () => {
    const newSearchParams = new URLSearchParams();
    newSearchParams.set("search_val", searchValue);
    newSearchParams.set("endpoint_type", endpointType);
    newSearchParams.set("page", currentPage.toString());
    history.push(`${location.pathname}?${newSearchParams.toString()}`);
  };
  
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [searchValue, setSearchValue] = useState(
    searchParams.get("search_val") || ""
  );
  const [endpointType, setEndpointType] = useState(
    searchParams.get("endpoint_type") || ""
  );
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  useEffect(() => {
    setData(mockData);

    const filtered = data.filter((item) => {
      return (
        (!searchValue ||
          item.name.toLowerCase().includes(searchValue.toLowerCase())) &&
        (!endpointType || item.type === endpointType)
      );
    });

    setFilteredData(filtered);
  }, [data, searchValue, endpointType]);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const displayedItems = filteredData.slice(indexOfFirstItem, indexOfLastItem);

  const handlePageChange = (page) => {
    setCurrentPage(page);
    handleFiltersAndPaginationChange
  };
  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleEndpointTypeChange = (e) => {
    setEndpointType(e.target.value);
  };

  return (
    <div className="endpoints">
      <h2>Серверы и ПК</h2>

      <div className="filters">
        <input
          type="text"
          placeholder="Поиск по названию"
          value={searchValue}
          onChange={handleSearchChange}
        />
        <select value={endpointType} onChange={handleEndpointTypeChange}>
          <option value="">Все типы</option>
          <option value="server">Сервер</option>
          <option value="pc">ПК</option>
        </select>
      </div>

      <table>
        <thead>
          <tr>
            <th>Название</th>
            <th>Тип</th>
            <th>Тэги</th>
            <th>Местоположение</th>
          </tr>
        </thead>
        <tbody>
          {displayedItems.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.type}</td>
              <td>{item.tags}</td>
              <td>{item.location}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Pagination
        currentPage={currentPage}
        totalPages={Math.ceil(filteredData.length / itemsPerPage)}
        onPageChange={handlePageChange}
      />
    </div>
  );
}

export default EndpointsTable;
